$(document).ready(function(){$window=$(window);if(navigator.userAgent.indexOf("iPhone")===-1&&navigator.userAgent.indexOf("iPad")===-1&&navigator.userAgent.indexOf("Blackberry")===-1&&navigator.userAgent.indexOf("Android")===-1){$window.resize(function(){setUpParallax($window)});setUpParallax($window)}});function setUpParallax(a){$("[data-type]").each(function(){$(this).data("offsetY",parseInt($(this).attr("data-offsetY")));$(this).data("Xposition",$(this).attr("data-Xposition"));$(this).data("speed",$(this).attr("data-speed"))});$('section[data-type="background"]').each(function(){var d=$(this),c=d.offset(),b=c.top;$(window).scroll(function(){if((a.scrollTop()+a.height())>(b)&&((b+d.height())>a.scrollTop())){var e=-(a.scrollTop()/d.data("speed"));if(a.width()<=991){e=0}else{if(d.data("offsetY")){e+=d.data("offsetY")}}var f="50% "+e+"px";d.css({backgroundPosition:f});d.css({top:e+"px"})}})})};